import styled from 'styled-components'
import { useEffect, useState } from 'react'
import {Svg} from '../../styled-components-general/svg'
import {Titulo} from '../../styled-components-general/titulo-central'

const ContenedorQueHacemos = styled.section`
  width:100%;
  display:grid;
  overflow:hidden;
  margin-bottom:5%;
  justify-content:center;
  grid-template-columns:15% 35% 35% 15%;
  grid-template-rows:auto auto auto auto;
  margin-top:calc(7% + ${props => props.QueHacemosMenuEstaFixed || '0'});

  @media (max-width: 945px){
    grid-template-rows:auto;
    grid-template-columns:reapeat(4, 1fr);
  }

`

const TituloSeccion = styled(Titulo)`
  grid-row:1/2;
  margin-top:12%;
  grid-column: 2/4;
  aling-self:center;
  justify-self: center;

  ::selection{
    color:#fff;
  }

`
const MenuServicios = styled.nav`
  height:50px;
  grid-row:2/3;
  grid-column: 2/4;
  user-select: none;
  justify-self:center;
`

const UlMenuServ = styled.ul`
  display:flex;
  padding:20px;
  font-size:18px;
  font-family:FontMenu;

  @media (max-width: 640px){
    font-size:0.8rem;
  }
`

const LiMenuServ = styled.li`
  color:#1b3281;
  cursor:pointer;
  margin-left:8px;
  margin-right:8px;

  :hover{
    border-bottom:4px solid #3d5bcc;
  }
`

const FichaServicio = styled.div`
  display:grid;
  grid-template-columns:25% 15% 60%;
  grid:'icono lista lista'
      'vacio vacio boton';
  color:#000;
  padding:20px;
  margin-top:4%;
  margin-left:8%;
  margin-right:8%;
  border:1px solid;
  margin-bottom:4%;
  border-radius:10px;
  grid-row:${props => props.fichaRow};
  grid-column:${props => props.fichaColum};

  @media (max-width: 640px){
    grid:'icono icono icono'
      'lista lista lista'
      'boton boton boton';
  }

  :hover{
    transform:scale(1.05);
  }

`

const UlFicha = styled.ul`
  width:80%;
  grid-area:lista;
  margin-top:5px;
`

const LiFicha = styled.li`
  padding:8px;
  margin-left:8%;
  text-align: start;
  font-family:FontMenu;

  @media (max-width: 640px){
    font-size:0.8rem;
  }

  ::selection{
    color:#fff;
    background:rgba(78,112,239,1);
  }
`

const Acarreo = styled(Svg)`
  transform:scale(1.3);

  @media (max-width: 640px){
    
  }
`

const Volquete = styled(Svg)`
  transform:scale(2);
`

const Desagote = styled(Svg)`
  transform:scale(1.2);
`

const Boton = styled.button`
  color:#000;
  padding:6px;
  font-size:15px;
  cursor:pointer;
  grid-area:boton;
  background:none;
  border-radius:5px;
  font-family:FontMenu;
`

